import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  title = 'Voter';

  addFlag = false;
  deleteFlag = false;
  updateFlag = false;
  getFlag = false;

  deleteFunction(){
    this.deleteFlag=true;
    console.log("delete work");

  }

  updateFunction(){
    this.updateFlag=true;
    console.log("update work");
  }

  getFunction(){
    this.getFlag=true;
    console.log("get work");
  }

  addFunction(){
    this.addFlag=true;
    console.log("add work");
  }
}
<nav class="navbar navbar-expand-lg navbar-light bg-light">
  <a class="navbar-brand" href="#">Navbar</a>
  <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav"
    aria-expanded="false" aria-label="Toggle navigation">
    <span class="navbar-toggler-icon"></span>
  </button>
  <div class="collapse navbar-collapse" id="navbarNav">
    <ul class="navbar-nav">
      <li class="nav-item active">
        <button class="nav-link" (click)="addFunction()">Add</button>
      </li>
      <li class="nav-item">
        <button class="nav-link" (click)="getFunction()">Display</button>
      </li>
      <li class="nav-item">
        <button class="nav-link" (click)="deleteFunction()">Delete</button>
      </li>
      <li class="nav-item">
        <button class="nav-link" (click)="updateFunction()">Update</button>
      </li>
    </ul>
  </div>
</nav>


<div *ngIf="addFlag == true">
  <app-add-voter></app-add-voter>



  <app-delete-voter *ngIf="deleteFlag == true"></app-delete-voter>



  <app-update-voter></app-update-voter>



  <app-display-voter *ngIf="getFlag == true"></app-display-voter>
