import { Component } from '@angular/core';
import { VoterService } from '../voter.service';

@Component({
  selector: 'app-add-voter',
  templateUrl: './add-voter.component.html',
  styleUrls: ['./add-voter.component.css'],
})
export class AddVoterComponent {
  firstName: any;
  middleName: any;
  lastName: any;
  permanentAddress: any;
  currentAddress: any;
  age: any;
  aadharNumber: any;
  email: any;

  constructor(private service: VoterService) {}

  clear() {
    this.firstName = '';
    this.middleName = '';
    this.lastName = '';
    this.permanentAddress = '';
    this.currentAddress = '';
    this.age = '';
    this.aadharNumber = '';
    this.email = '';
  }

  submit() {
    if (
      this.firstName != '' &&
      this.middleName != null &&
      this.lastName != null &&
      this.permanentAddress != null &&
      this.currentAddress != null &&
      this.age != null &&
      this.aadharNumber != null &&
      this.email != null
    ) {
      let obj = {
        firstName: this.firstName,
        middleName: this.middleName,
        lastName: this.lastName,
        permanentAddress: this.permanentAddress,
        currentAddress: this.currentAddress,
        age: this.age,
        aadharNumber: this.aadharNumber,
        email: this.email,
      };
      console.log(obj);
      alert("Voter added")
      this.service.addVoter(obj).subscribe((data) => console.log('saved'));
    }
    else{
      alert("Please enter the all fields..!")
    }
    this.clear();
  }
}
<div class="outer">

  <div class="row">
    <div class="col">
      <input type="text" class="form-control" placeholder="First name" [(ngModel)]="firstName">
    </div>
    <div class="col">
      <input type="text" class="form-control" placeholder="Middle name" [(ngModel)]="middleName">
    </div>
    <div class="col">
      <input type="text" class="form-control" placeholder="Last name" [(ngModel)]="lastName">
    </div>
  </div>
<br>
  <div class="row">
    <div class="col">
      <input type="text" class="form-control" placeholder="Permanent Address" [(ngModel)]="permanentAddress">
    </div>
    <div class="col">
      <input type="text" class="form-control" placeholder="Current Addres" [(ngModel)]="currentAddress">
    </div>
  </div>
<br>
  <div class="row">
    <div class="col">
      <input type="number" class="form-control" placeholder="Age" [(ngModel)]="age">
    </div>

    <div class="col">
      <input type="text" class="form-control" placeholder="Aadhar Number" [(ngModel)]="aadharNumber">
    </div>
  </div>
<br>
  <div class="row">
    <div class="col">
      <input type="email" class="form-control" placeholder="Email" [(ngModel)]="email">
    </div>
  </div>
<br>
  <div class="col" style="margin-left: 40%;">
    <button class="btn btn-success" (click)="submit()">SUBMIT</button>
  </div>
</div>
