import { Component } from '@angular/core';
import { VoterService } from '../voter.service';

@Component({
  selector: 'app-display-voter',
  templateUrl: './display-voter.component.html',
  styleUrls: ['./display-voter.component.css']
})
export class DisplayVoterComponent {

constructor(private service:VoterService){}

voterList:any;
flag = false;
ngOnInit():void{
this.service.getAllVoters().subscribe(data => this.voterList = data);

console.log(this.voterList);

}

}
<div class="outer" *ngIf="flag==true">
  <table class="table table-bordered">
      <tr>
        <th scope="col">Id</th>
        <th scope="col">First Name</th>
        <th scope="col">Middle Name</th>
        <th scope="col">Last Name</th>
        <th scope="col">Permanent Address</th>
        <th scope="col">Current Address</th>
        <th scope="col">Age</th>
        <th scope="col">Aadhar Number</th>
        <th scope="col">Email</th>
      </tr>

      <tr *ngFor="let a of voterList">
        <td>{{a.id}}</td>
        <td>{{a.firstName}}</td>
        <td>{{a.middleName}}</td>
        <td>{{a.lastName}}</td>
        <td>{{a.permanentAddress}}</td>
        <td>{{a.currentAddress}}</td>
        <td>{{a.age}}</td>
        <td>{{a.aadharNumber}}</td>
        <td>{{a.email}}</td>
      </tr>
  </table>
</div>
