import { Component } from '@angular/core';
import { VoterService } from '../voter.service';

@Component({
  selector: 'app-delete-voter',
  templateUrl: './delete-voter.component.html',
  styleUrls: ['./delete-voter.component.css']
})
export class DeleteVoterComponent {

  constructor(private service:VoterService){}

  voterList:any;

  ngOnInit():void{
  this.service.getAllVoters().subscribe(data=> this.voterList = data);
  console.log(this.voterList);
  }

  showAllUser(){

  }

  deleteVoter(a:any){
    alert("Voter Deleted Successfully");
    console.log(a);
    this.service.deleteVoter(a).subscribe();
    this.ngOnInit();
  }


}
<div class="outer">
  <table class="table table-bordered" style="text-align: center;">
      <tr>
        <th scope="col">Id</th>
        <th scope="col">First Name</th>
        <th scope="col">Middle Name</th>
        <th scope="col">Last Name</th>
        <th scope="col">Permanent Address</th>
        <th scope="col">Current Address</th>
        <th scope="col">Age</th>
        <th scope="col">Aadhar Number</th>
        <th scope="col">Email</th>
        <th scope="col">Action</th>
      </tr>

      <tr *ngFor="let a of voterList">
        <td>{{a.id}}</td>
        <td>{{a.firstName}}</td>
        <td>{{a.middleName}}</td>
        <td>{{a.lastName}}</td>
        <td>{{a.permanentAddress}}</td>
        <td>{{a.currentAddress}}</td>
        <td>{{a.age}}</td>
        <td>{{a.aadharNumber}}</td>
        <td>{{a.email}}</td>
        <td><button type="button" class="btn btn-warning" (click)="deleteVoter(a.id)">Delete</button></td>
      </tr>
  </table>
</div>
